import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Avatar,
  Box,
  Button,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Tabs,
  Tab,
  Divider,
  LinearProgress,
  Card,
  CardContent,
  IconButton,
  Menu,
  MenuItem,
} from '@mui/material';
import {
  Edit,
  EmojiEvents,
  Timeline,
  History,
  TrendingUp,
  MoreVert,
  School,
  Star,
  Grade,
} from '@mui/icons-material';

const Profile = () => {
  const [selectedTab, setSelectedTab] = useState(0);
  const [anchorEl, setAnchorEl] = useState(null);
  const [loading, setLoading] = useState(true);
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Simulated API call to fetch user data
    const fetchUserData = async () => {
      try {
        // Replace with actual API call
        const mockUserData = {
          id: 1,
          username: 'ChessMaster2024',
          firstName: 'John',
          lastName: 'Doe',
          email: 'john.doe@example.com',
          rating: 1850,
          gamesPlayed: 245,
          winRate: 65,
          memberSince: '2023-01-15',
          achievements: [
            { id: 1, title: 'First Win', description: 'Won your first game', date: '2023-01-16' },
            { id: 2, title: 'Quick Learner', description: 'Completed 10 lessons', date: '2023-02-01' },
            { id: 3, title: 'Tournament Victor', description: 'Won a tournament', date: '2023-03-15' },
          ],
          recentGames: [
            { id: 1, opponent: 'Alice', result: 'win', rating: 1800, date: '2024-01-15' },
            { id: 2, opponent: 'Bob', result: 'loss', rating: 1900, date: '2024-01-14' },
            { id: 3, opponent: 'Charlie', result: 'win', rating: 1750, date: '2024-01-13' },
          ],
          stats: {
            openings: {
              favorite: 'Sicilian Defense',
              winRate: 70,
              gamesPlayed: 85,
            },
            progression: [
              { date: '2023-12', rating: 1750 },
              { date: '2024-01', rating: 1850 },
            ],
          },
        };

        setUserData(mockUserData);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching user data:', error);
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  const handleMenuClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  if (loading || !userData) {
    return (
      <Container maxWidth="lg">
        <LinearProgress />
      </Container>
    );
  }

  const getResultColor = (result) => {
    switch (result) {
      case 'win':
        return 'success.main';
      case 'loss':
        return 'error.main';
      default:
        return 'text.secondary';
    }
  };

  return (
    <Container maxWidth="lg">
      <Grid container spacing={3}>
        {/* Profile Header */}
        <Grid item xs={12}>
          <Paper sx={{ p: 3, position: 'relative' }}>
            <IconButton
              sx={{ position: 'absolute', top: 8, right: 8 }}
              onClick={handleMenuClick}
            >
              <MoreVert />
            </IconButton>
            <Menu
              anchorEl={anchorEl}
              open={Boolean(anchorEl)}
              onClose={handleMenuClose}
            >
              <MenuItem onClick={handleMenuClose}>Edit Profile</MenuItem>
              <MenuItem onClick={handleMenuClose}>Settings</MenuItem>
            </Menu>

            <Grid container spacing={3} alignItems="center">
              <Grid item>
                <Avatar
                  sx={{ width: 100, height: 100 }}
                  src="/avatar.jpg"
                  alt={userData.username}
                />
              </Grid>
              <Grid item xs>
                <Typography variant="h4" gutterBottom>
                  {userData.username}
                </Typography>
                <Typography variant="subtitle1" color="text.secondary">
                  {`${userData.firstName} ${userData.lastName}`}
                </Typography>
                <Box sx={{ mt: 1 }}>
                  <Button
                    variant="contained"
                    startIcon={<Edit />}
                    sx={{ mr: 1 }}
                  >
                    Edit Profile
                  </Button>
                </Box>
              </Grid>
              <Grid item xs={12} sm="auto">
                <Box sx={{ textAlign: { xs: 'left', sm: 'right' } }}>
                  <Typography variant="h6">
                    Rating: {userData.rating}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Member since {new Date(userData.memberSince).toLocaleDateString()}
                  </Typography>
                </Box>
              </Grid>
            </Grid>
          </Paper>
        </Grid>

        {/* Stats Cards */}
        <Grid item xs={12}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6} md={3}>
              <Card>
                <CardContent>
                  <Typography color="text.secondary" gutterBottom>
                    Сыграно партий
                  </Typography>
                  <Typography variant="h4">
                    {userData.gamesPlayed}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Card>
                <CardContent>
                  <Typography color="text.secondary" gutterBottom>
                    Процент побед
                  </Typography>
                  <Typography variant="h4">
                    {userData.winRate}%
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Card>
                <CardContent>
                  <Typography color="text.secondary" gutterBottom>
                    Любимый дебют
                  </Typography>
                  <Typography variant="h6">
                    {userData.stats.openings.favorite}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Card>
                <CardContent>
                  <Typography color="text.secondary" gutterBottom>
                    Достижения
                  </Typography>
                  <Typography variant="h4">
                    {userData.achievements.length}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </Grid>

        {/* Tabs Section */}
        <Grid item xs={12}>
          <Paper sx={{ width: '100%' }}>
            <Tabs
              value={selectedTab}
              onChange={handleTabChange}
              variant="scrollable"
              scrollButtons="auto"
            >
              <Tab icon={<History />} label="Recent Games" />
              <Tab icon={<EmojiEvents />} label="Achievements" />
              <Tab icon={<Timeline />} label="Statistics" />
              <Tab icon={<School />} label="Learning Progress" />
            </Tabs>

            <Box sx={{ p: 3 }}>
              {selectedTab === 0 && (
                <List>
                  {userData.recentGames.map((game) => (
                    <React.Fragment key={game.id}>
                      <ListItem>
                        <ListItemIcon>
                          <Grade sx={{ color: getResultColor(game.result) }} />
                        </ListItemIcon>
                        <ListItemText
                          primary={`vs ${game.opponent} (${game.rating})`}
                          secondary={new Date(game.date).toLocaleDateString()}
                        />
                        <Typography
                          variant="body2"
                          sx={{ color: getResultColor(game.result) }}
                        >
                          {game.result.toUpperCase()}
                        </Typography>
                      </ListItem>
                      <Divider variant="inset" component="li" />
                    </React.Fragment>
                  ))}
                </List>
              )}

              {selectedTab === 1 && (
                <List>
                  {userData.achievements.map((achievement) => (
                    <React.Fragment key={achievement.id}>
                      <ListItem>
                        <ListItemIcon>
                          <Star color="primary" />
                        </ListItemIcon>
                        <ListItemText
                          primary={achievement.title}
                          secondary={achievement.description}
                        />
                        <Typography variant="caption" color="text.secondary">
                          {new Date(achievement.date).toLocaleDateString()}
                        </Typography>
                      </ListItem>
                      <Divider variant="inset" component="li" />
                    </React.Fragment>
                  ))}
                </List>
              )}

              {selectedTab === 2 && (
                <Box>
                  <Typography variant="h6" gutterBottom>
                    Rating Progress
                  </Typography>
                  {/* Add a chart component here for rating progress */}
                  <Typography variant="body1" color="text.secondary">
                    Rating progress visualization will be added here
                  </Typography>
                </Box>
              )}

              {selectedTab === 3 && (
                <Box>
                  <Typography variant="h6" gutterBottom>
                    Learning Progress
                  </Typography>
                  <List>
                    <ListItem>
                      <ListItemIcon>
                        <TrendingUp color="primary" />
                      </ListItemIcon>
                      <ListItemText
                        primary="Completed Lessons"
                        secondary="15 out of 30"
                      />
                      <LinearProgress
                        variant="determinate"
                        value={50}
                        sx={{ width: 100 }}
                      />
                    </ListItem>
                  </List>
                </Box>
              )}
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Profile;
