import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Card,
  CardContent,
  CardMedia,
  CardActionArea,
  Tabs,
  Tab,
  Box,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Chip,
  LinearProgress,
} from '@mui/material';
import {
  School,
  EmojiObjects,
  Extension,
  Timeline,
  Star,
  CheckCircle,
} from '@mui/icons-material';

const Learn = () => {
  const [selectedTab, setSelectedTab] = useState(0);
  const [lessons, setLessons] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulated API call to fetch lessons
    const fetchLessons = async () => {
      try {
        // Replace with actual API call
        const mockLessons = [
          {
            id: 1,
            title: 'Basic Chess Rules',
            description: 'Learn how each piece moves and the basic rules of chess',
            category: 'basics',
            difficulty: 'beginner',
            duration: '30 min',
            progress: 100,
            image: 'https://example.com/chess-basics.jpg',
          },
          {
            id: 2,
            title: 'Common Opening Principles',
            description: 'Master the fundamental principles of chess openings',
            category: 'openings',
            difficulty: 'intermediate',
            duration: '45 min',
            progress: 60,
            image: 'https://example.com/chess-openings.jpg',
          },
          {
            id: 3,
            title: 'Tactical Patterns',
            description: 'Learn to spot common tactical patterns and combinations',
            category: 'tactics',
            difficulty: 'intermediate',
            duration: '60 min',
            progress: 30,
            image: 'https://example.com/chess-tactics.jpg',
          },
          // Add more lessons here
        ];

        setLessons(mockLessons);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching lessons:', error);
        setLoading(false);
      }
    };

    fetchLessons();
  }, []);

  const categories = [
    { label: 'All', icon: <School /> },
    { label: 'Basics', icon: <EmojiObjects /> },
    { label: 'Tactics', icon: <Extension /> },
    { label: 'Strategy', icon: <Timeline /> },
  ];

  const filteredLessons = selectedTab === 0
    ? lessons
    : lessons.filter(lesson => 
        lesson.category.toLowerCase() === categories[selectedTab].label.toLowerCase()
      );

  const getDifficultyColor = (difficulty) => {
    switch (difficulty.toLowerCase()) {
      case 'beginner':
        return 'success';
      case 'intermediate':
        return 'warning';
      case 'advanced':
        return 'error';
      default:
        return 'default';
    }
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" gutterBottom>
          Learn Chess
        </Typography>
        <Typography variant="subtitle1" color="text.secondary">
          Master the game with our comprehensive learning materials and interactive lessons
        </Typography>
      </Box>

      <Paper sx={{ mb: 4 }}>
        <Tabs
          value={selectedTab}
          onChange={(e, newValue) => setSelectedTab(newValue)}
          variant="scrollable"
          scrollButtons="auto"
        >
          {categories.map((category, index) => (
            <Tab
              key={category.label}
              icon={category.icon}
              label={category.label}
              value={index}
            />
          ))}
        </Tabs>
      </Paper>

      {loading ? (
        <LinearProgress />
      ) : (
        <Grid container spacing={3}>
          {filteredLessons.map((lesson) => (
            <Grid item xs={12} sm={6} md={4} key={lesson.id}>
              <Card>
                <CardActionArea>
                  <CardMedia
                    component="div"
                    sx={{
                      height: 140,
                      backgroundColor: 'grey.300',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <School sx={{ fontSize: 60, color: 'grey.500' }} />
                  </CardMedia>
                  <CardContent>
                    <Typography gutterBottom variant="h6" component="div">
                      {lesson.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                      {lesson.description}
                    </Typography>
                    <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                      <Chip
                        size="small"
                        label={lesson.difficulty}
                        color={getDifficultyColor(lesson.difficulty)}
                      />
                      <Chip
                        size="small"
                        label={lesson.duration}
                        variant="outlined"
                      />
                    </Box>
                    <LinearProgress
                      variant="determinate"
                      value={lesson.progress}
                      sx={{ height: 8, borderRadius: 4 }}
                    />
                    <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
                      <Typography variant="body2" color="text.secondary">
                        Progress: {lesson.progress}%
                      </Typography>
                      {lesson.progress === 100 && (
                        <CheckCircle
                          color="success"
                          sx={{ ml: 1, fontSize: 16 }}
                        />
                      )}
                    </Box>
                  </CardContent>
                </CardActionArea>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}

      <Paper sx={{ mt: 4, p: 3 }}>
        <Typography variant="h6" gutterBottom>
          Your Learning Path
        </Typography>
        <List>
          <ListItem>
            <ListItemIcon>
              <Star color="primary" />
            </ListItemIcon>
            <ListItemText
              primary="Current Level: Intermediate"
              secondary="Keep going! You're making great progress"
            />
          </ListItem>
          <ListItem>
            <ListItemIcon>
              <Timeline color="primary" />
            </ListItemIcon>
            <ListItemText
              primary="Completed Lessons: 5"
              secondary="15 lessons remaining in your current path"
            />
          </ListItem>
        </List>
      </Paper>
    </Container>
  );
};

export default Learn;
