import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Button,
  Box,
  Card,
  CardContent,
  CardMedia,
  CardActionArea,
  Stack,
} from '@mui/material';
import {
  SportsEsports,
  School,
  EmojiEvents,
  TrendingUp,
  SmartToy,
  Group,
} from '@mui/icons-material';

const Home = () => {
  const navigate = useNavigate();

  const features = [
    {
      icon: <SportsEsports fontSize="large" />,
      title: 'Играть в шахматы',
      description: 'Сражайтесь с игроками со всего мира или тренируйтесь против нашего ИИ',
      action: () => navigate('/play'),
    },
    {
      icon: <School fontSize="large" />,
      title: 'Обучение',
      description: 'Интерактивные уроки и руководства для всех уровней подготовки',
      action: () => navigate('/learn'),
    },
    {
      icon: <EmojiEvents fontSize="large" />,
      title: 'Турниры',
      description: 'Участвуйте в онлайн-турнирах и выигрывайте призы',
      action: () => navigate('/tournaments'),
    },
  ];

  const stats = [
    {
      value: '1M+',
      label: 'Игроков',
      icon: <Group />,
    },
    {
      value: '10M+',
      label: 'Сыгранных партий',
      icon: <SportsEsports />,
    },
    {
      value: '500+',
      label: 'Уроков',
      icon: <School />,
    },
    {
      value: '100+',
      label: 'Стран',
      icon: <TrendingUp />,
    },
  ];

  return (
    <>
      {/* Hero Section */}
      <Box
        sx={{
          bgcolor: 'primary.main',
          color: 'white',
          py: 8,
          mb: 6,
        }}
      >
        <Container maxWidth="lg">
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12} md={6}>
              <Typography variant="h2" gutterBottom>
                Освойте игру в шахматы
              </Typography>
              <Typography variant="h5" paragraph>
                Учитесь, играйте и совершенствуйте свои шахматные навыки с помощью нашей комплексной платформы
              </Typography>
              <Stack direction="row" spacing={2}>
                <Button
                  variant="contained"
                  color="secondary"
                  size="large"
                  onClick={() => navigate('/play')}
                >
                  Играть сейчас
                </Button>
                <Button
                  variant="outlined"
                  color="inherit"
                  size="large"
                  onClick={() => navigate('/learn')}
                >
                  Начать обучение
                </Button>
              </Stack>
            </Grid>
            <Grid item xs={12} md={6}>
              <Box
                component="img"
                src="/chess-hero.png"
                alt="Chess"
                sx={{
                  width: '100%',
                  maxWidth: 500,
                  height: 'auto',
                  display: 'block',
                  margin: 'auto',
                }}
              />
            </Grid>
          </Grid>
        </Container>
      </Box>

      {/* Features Section */}
      <Container maxWidth="lg" sx={{ mb: 6 }}>
        <Grid container spacing={4}>
          {features.map((feature) => (
            <Grid item xs={12} md={4} key={feature.title}>
              <Card sx={{ height: '100%' }}>
                <CardActionArea onClick={feature.action}>
                  <CardContent>
                    <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
                      {React.cloneElement(feature.icon, { color: 'primary', sx: { fontSize: 48 } })}
                    </Box>
                    <Typography variant="h5" component="div" align="center" gutterBottom>
                      {feature.title}
                    </Typography>
                    <Typography variant="body1" color="text.secondary" align="center">
                      {feature.description}
                    </Typography>
                  </CardContent>
                </CardActionArea>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>

      {/* Stats Section */}
      <Box sx={{ bgcolor: 'grey.100', py: 6, mb: 6 }}>
        <Container maxWidth="lg">
          <Grid container spacing={4}>
            {stats.map((stat) => (
              <Grid item xs={6} md={3} key={stat.label}>
                <Paper
                  elevation={0}
                  sx={{
                    p: 3,
                    textAlign: 'center',
                    backgroundColor: 'transparent',
                  }}
                >
                  {React.cloneElement(stat.icon, {
                    color: 'primary',
                    sx: { fontSize: 40, mb: 1 },
                  })}
                  <Typography variant="h4" component="div" gutterBottom>
                    {stat.value}
                  </Typography>
                  <Typography variant="subtitle1" color="text.secondary">
                    {stat.label}
                  </Typography>
                </Paper>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* AI Feature Section */}
      <Container maxWidth="lg" sx={{ mb: 6 }}>
        <Grid container spacing={4} alignItems="center">
          <Grid item xs={12} md={6}>
            <Typography variant="h3" gutterBottom>
              Играйте против продвинутого ИИ
            </Typography>
            <Typography variant="body1" paragraph>
              Сразитесь с нашим сложным шахматным ИИ, работающим на основе новейших технологий машинного обучения.
              Идеально подходит для игроков всех уровней, наш ИИ адаптируется к вашей силе игры, чтобы обеспечить
              наиболее увлекательный и образовательный опыт.
            </Typography>
            <Button
              variant="contained"
              color="primary"
              size="large"
              startIcon={<SmartToy />}
              onClick={() => navigate('/play')}
            >
              Сразиться с ИИ
            </Button>
          </Grid>
          <Grid item xs={12} md={6}>
            <Box
              sx={{
                bgcolor: 'grey.200',
                borderRadius: 2,
                p: 4,
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                minHeight: 300,
              }}
            >
              <SmartToy sx={{ fontSize: 120, color: 'primary.main' }} />
            </Box>
          </Grid>
        </Grid>
      </Container>
    </>
  );
};

export default Home;
