import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Card,
  CardContent,
  CardMedia,
  CardActionArea,
  Chip,
  Box,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar,
  Button,
  TextField,
  InputAdornment,
  CircularProgress,
} from '@mui/material';
import {
  Search,
  Event,
  TrendingUp,
  NewReleases,
  EmojiEvents,
} from '@mui/icons-material';

const News = () => {
  const [news, setNews] = useState([]);
  const [tournaments, setTournaments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    // Simulated API call to fetch news and tournaments
    const fetchNewsAndTournaments = async () => {
      try {
        // Replace with actual API calls
        const mockNews = [
          {
            id: 1,
            title: 'Magnus Carlsen Wins Super Tournament',
            description: 'World champion continues his dominant performance...',
            category: 'Tournament',
            date: '2024-01-15',
            image: 'https://example.com/carlsen.jpg',
            author: 'Chess News',
            readTime: '5 min',
          },
          {
            id: 2,
            title: 'New Chess AI Breakthrough',
            description: 'Revolutionary neural network architecture for chess...',
            category: 'Technology',
            date: '2024-01-14',
            image: 'https://example.com/ai-chess.jpg',
            author: 'Tech Chess',
            readTime: '8 min',
          },
          {
            id: 3,
            title: 'Rising Stars in Chess 2024',
            description: 'Meet the young prodigies taking the chess world by storm...',
            category: 'Players',
            date: '2024-01-13',
            image: 'https://example.com/prodigies.jpg',
            author: 'Chess Weekly',
            readTime: '10 min',
          },
        ];

        const mockTournaments = [
          {
            id: 1,
            name: 'World Chess Championship 2024',
            location: 'London, UK',
            date: '2024-06-01',
            prize: '$1,000,000',
            status: 'Upcoming',
          },
          {
            id: 2,
            name: 'Grand Chess Tour',
            location: 'Various Locations',
            date: '2024-03-15',
            prize: '$350,000',
            status: 'Registration Open',
          },
          {
            id: 3,
            name: 'European Chess Championship',
            location: 'Berlin, Germany',
            date: '2024-04-01',
            prize: '€200,000',
            status: 'Upcoming',
          },
        ];

        setNews(mockNews);
        setTournaments(mockTournaments);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching news:', error);
        setLoading(false);
      }
    };

    fetchNewsAndTournaments();
  }, []);

  const filteredNews = news.filter(item =>
    item.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    item.description.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <Container maxWidth="lg">
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" gutterBottom>
          Шахматные новости и события
        </Typography>
        <Typography variant="subtitle1" color="text.secondary">
          Будьте в курсе последних новостей, турниров и обновлений сообщества
        </Typography>
      </Box>

      <Grid container spacing={3}>
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 2, mb: 3 }}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Поиск новостей..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
          </Paper>

          {loading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
              <CircularProgress />
            </Box>
          ) : (
            filteredNews.map((item) => (
              <Card key={item.id} sx={{ mb: 3 }}>
                <CardActionArea>
                  <CardMedia
                    component="div"
                    sx={{
                      height: 200,
                      backgroundColor: 'grey.300',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <NewReleases sx={{ fontSize: 60, color: 'grey.500' }} />
                  </CardMedia>
                  <CardContent>
                    <Box sx={{ display: 'flex', gap: 1, mb: 1 }}>
                      <Chip
                        size="small"
                        label={item.category}
                        color="primary"
                        variant="outlined"
                      />
                      <Chip
                        size="small"
                        label={item.readTime}
                        variant="outlined"
                      />
                    </Box>
                    <Typography gutterBottom variant="h5" component="div">
                      {item.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" paragraph>
                      {item.description}
                    </Typography>
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                      <Typography variant="caption" color="text.secondary">
                        Автор {item.author} • {new Date(item.date).toLocaleDateString('ru-RU')}
                      </Typography>
                    </Box>
                  </CardContent>
                </CardActionArea>
                <Box sx={{ p: 2, pt: 0 }}>
                  <Button size="small" color="primary" fullWidth>
                    Читать далее
                  </Button>
                </Box>
              </Card>
            ))
          )}
        </Grid>

        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2, mb: 3 }}>
            <Typography variant="h6" gutterBottom>
              Популярные темы
            </Typography>
            <List>
              {['Чемпионат мира', 'Онлайн-шахматы', 'Шахматы 960', 'Обучение шахматам'].map((topic) => (
                <ListItem key={topic} button>
                  <ListItemAvatar>
                    <Avatar>
                      <TrendingUp />
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText primary={topic} />
                </ListItem>
              ))}
            </List>
          </Paper>

          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Предстоящие турниры
            </Typography>
            <List>
              {tournaments.map((tournament) => (
                <React.Fragment key={tournament.id}>
                  <ListItem alignItems="flex-start">
                    <ListItemAvatar>
                      <Avatar>
                        <EmojiEvents />
                      </Avatar>
                    </ListItemAvatar>
                    <ListItemText
                      primary={tournament.name}
                      secondary={
                        <Box component="span">
                          <Typography
                            component="span"
                            variant="body2"
                            color="text.primary"
                          >
                            {tournament.location}
                          </Typography>
                          {` — ${tournament.date}`}
                          <br />
                          {`Призовой фонд: ${tournament.prize}`}
                          <br />
                          <Chip
                            size="small"
                            label={tournament.status === 'Upcoming' ? 'Предстоящий' : 
                                   tournament.status === 'Registration Open' ? 'Открыта регистрация' : 
                                   tournament.status}
                            color="primary"
                            sx={{ mt: 1 }}
                          />
                        </Box>
                      }
                    />
                  </ListItem>
                  <Divider variant="inset" component="li" />
                </React.Fragment>
              ))}
            </List>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default News;
